        $('#Result').html("");
        await gf.createGift("Big")
        $('#Result').html(gf.message);
        $('#Sled').html(gf.sled.displaySled())
    });


    $('#Deliver').click(async function () {
        await gf.deliverSled()
        $('#Sled').html(gf.sled)
    });
});

