// you can import css in your js file!
import 'bootstrap/dist/css/bootstrap.css'
import './index.css'
import './gift.js'
// If you want to use jquery
import $ from 'jquery'

// TODO
const sled = []

$(document).ready(function () {
    $('#1').click(function () {
        const smallGift = giftFactory("Small")
        smallGift.prepareGift()
        addGift(smallGift);
        console.log(sled)
    });
});

function addGift(gift) {
    for (var i = 0; i < sled.length; i++) {
        sum += sled[i]
    }
    console.log(sum)

    sled.push(gift)

}


class Gift {
    constructor(weight, preparation_time) {
        this.weight = weight
        this.preparation_time = preparation_time
    }

    prepareGift() {
        setTimeout(function () {
            console.log("test set timeout")
        }, (this.preparation_time * 1000));
    }

}

class SmallGift extends Gift {
    constructor() {
        super(1, 0.5)
    }
}

class NormalGift extends Gift {
    constructor() {
        super(2, 1)
    }
}

class BigGift extends Gift {
    constructor() {
        super(5, 2)
    }
}


const giftFactory = (type) => {
    switch (type) {
        case "Small":
            return new SmallGift();
        case "Normal":
            return new NormalGift();
        case "Big":
            return new BigGift();
        default:
            return null;
    }
}
