import {giftFactory} from './gift.js'
import {Sled} from './sled.js'
import {Dwarf} from './dwarf.js'
export class GiftsFactory {
    constructor() {
        this.dwarf = new Dwarf();
        this.sled = new Sled();
    }

    getDwarf() {
        return this.dwarf
    }

    getSled() {
        return this.sled
    }

    async createGift(){
        try {
            const smallGift = giftFactory("Small")
            if (!sled.isTooHeavy(smallGift) && !dwarf.getIsWorking()) {
                dwarf.setIsWorking(true)
                await dwarf.prepareGift(smallGift)
                dwarf.addGift(sled, smallGift)
                console.log(sled)
            }
        } catch (err) {
            console.log(err)
        }
    }
}
