import {
    Sled
} from './sled.js'
import {
    Dwarf
} from './dwarf.js'

export class GiftsFactory {
    constructor() {
        this.dwarf = new Dwarf();
        this.sled = new Sled();
        this.message = ""
    }

    displayGiftStatus(type) {
        return this.message = this.dwarf.createGift(type, this.sled)
    }

    displaySledStatus() {
        return this.message = this.sled.deliver(this.dwarf)
    }

    async reset(){
        const a = await this.sled.reset()
        const b = await this.dwarf.reset()
        return this.message= a+b
    }

}
