                this.gifts = []
                $('#Sled').html("")
                this.sled.isDelivering = false;
                return this.message = "Gifts have been delivered"
            })
            .catch(function () {
                isDelivering = false;
                return this.message = "These stupids reindeers are hungry, try again !"
            });


        }
    }

