// you can import css in your js file!
import 'bootstrap/dist/css/bootstrap.css'
import './index.css'
// If you want to use jquery
import $ from 'jquery'
const axios = require('axios');

// TODO
let sled = []

$(document).ready(function () {
    $('#Small').click(function () {
        const smallGift = giftFactory("Small")
        smallGift.prepareGift()
        $('#Text').html(addGift(smallGift));
        $('#Sled').html(displaySled())
    });

    $('#Normal').click(function () {
        const normalGift = giftFactory("Normal")
        normalGift.prepareGift()
        $('#Text').html(addGift(normalGift));
        $('#Sled').html(displaySled())
    });

    $('#Big').click(function () {
        const bigGift = giftFactory("Big")
        bigGift.prepareGift()
        $('#Text').html(addGift(bigGift));
        $('#Sled').html(displaySled())
    });

    $('#Deliver').click(function () {
         deliver()
    });
});

function displaySled(){
    let str =""
    for (let i = 0; i < sled.length; i++) {
        str += "<li> Gift "+(i+1)+ " : " + sled[i].constructor.name +"</li>"
    }
    return str
}

function addGift(gift) {
    let sum =0
    for (let i = 0; i < sled.length; i++) {
        sum += sled[i].weight
    }
    sum+=gift.weight
    if(sum<=12){
        sled.push(gift)
        return "ok"
    }
    else{
        return "it's to heavy"
    }
}

function deliver(){
    axios.post('http://localhost:8081', {
        gifts : sled
      })
      .then(function (response) {
        sled=[]
        $('#Sled').html("")
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
}


class Gift {
    constructor(weight, preparation_time) {
        this.weight = weight
        this.preparation_time = preparation_time
    }

    prepareGift() {
        f3()
        async function f3() {
            try{
                await console.log(test().resolve())
            }catch{
                console.error("catch")
            }
        }
    }

}

function test(time) {
    return new Promise(resolve => {
      setTimeout(() => {
        resolve('resolved');
        console.log("test")
      }, (time * 1000));
    });
  }

class SmallGift extends Gift {
    constructor() {
        super(1, 0.5)
    }
}

class NormalGift extends Gift {
    constructor() {
        super(2, 1)
    }
}

class BigGift extends Gift {
    constructor() {
        super(5, 2)
    }
}


const giftFactory = (type) => {
    switch (type) {
        case "Small":
            return new SmallGift();
        case "Normal":
            return new NormalGift();
        case "Big":
            return new BigGift();
        default:
            return null;
    }
}
