export class Sled {
    constructor() {
        this.gifts = []
        this.isDelivering = false;
    }

    isTooHeavy(gift) {
        let sum = 0
        for (let i = 0; i < this.gifts.length; i++) {
            sum += this.gifts[i].weight
        }
        sum += gift.weight
        if (sum <= 12) {
            return false
        } else {
            return true
        }
    }

    displaySled() {
        let str = ""
        for (let i = 0; i < this.gifts.length; i++) {
            str += "<li> Gift " + (i + 1) + " : " + this.gifts[i].constructor.name + "</li>"
        }
        return str
    }

    deliver(){
        if (!this.isDelivering) {
            if (this.gifts.length > 0) {
                isDelivering = true;
                axios.post('http://localhost:8081', {
                        gifts: this.gifts
                    })
                    .then(function () {
                        this.gifts = []
                        $('#Sled').html("")
                        $('#Result').html("Gifts have been delivered")
                        isdelivering = false;
                    })
                    .catch(function () {
                        isdelivering = false;
                        $('#Result').html("These stupids reindeers are hungry, try again !")
                    });
            } else {
                $('#Result').html("Sled empty")
            }
        } else {
            $('#Result').html("Reindeers are delivering !")
        }
    }
}
