import 'bootstrap/dist/css/bootstrap.css'
import './index.css'
// If you want to use jquery
import $ from 'jquery'
const axios = require('axios');

// TODO
let sled = []
let isworking = false;

$(document).ready(function () {
    $('#Small').click(async function () {
        if (!isworking) {
            const smallGift = giftFactory("Small")
            try {
                $('#Text').html("in work")
                isworking = true
                await smallGift.prepareGift()
                $('#Text').html(addGift(smallGift));
                $('#Sled').html(displaySled())
            } catch (err) {
                return err
            }
        }else{
            $('#Text').html("dwarf is busy")
        }
    });

    $('#Normal').click(async function () {
        if (!isworking) {
            const normalGift = giftFactory("Normal")
            try {
                $('#Text').html("in work")
                isworking = true
                await normalGift.prepareGift()
                $('#Text').html(addGift(normalGift));
                $('#Sled').html(displaySled())
            } catch (err) {
                return err
            }
        }else{
            $('#Text').html("dwarf is busy")
        }
    });


    $('#Big').click(async function () {
        if (!isworking) {
            const bigGift = giftFactory("Big")
            try {
                $('#Text').html("in work")
                isworking = true
                await bigGift.prepareGift()
                $('#Text').html(addGift(bigGift));
                $('#Sled').html(displaySled())
            } catch (err) {
                return err
            }
        }else{
            $('#Text').html("dwarf is busy")
        }
    });

    $('#Deliver').click(function () {
        deliver()
    });
});

function displaySled() {
    let str = ""
    for (let i = 0; i < sled.length; i++) {
        str += "<li> Gift " + (i + 1) + " : " + sled[i].constructor.name + "</li>"
    }
    return str
}

function addGift(gift) {
    let sum = 0
    for (let i = 0; i < sled.length; i++) {
        sum += sled[i].weight
    }
    sum += gift.weight
    if (sum <= 12) {
        sled.push(gift)
        return "ok"
    } else {
        return "it's to heavy"
    }
}

function deliver() {
    if(sled.length>0){
    axios.post('http://localhost:8081', {
            gifts: sled
        })
        .then(function (response) {
            sled = []
            $('#Sled').html("")
            console.log(response);
        })
        .catch(function (error) {
            console.log(error);
        });
    }else{
        $('#Text').html("Sled empty")
    }
}


class Gift {
    constructor(weight, preparation_time) {
        this.weight = weight
        this.preparation_time = preparation_time
    }

    prepareGift() {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                isworking = false
                resolve("ok2")


            }, (this.preparation_time * 1000))
        })
    }

}

class SmallGift extends Gift {
    constructor() {
        super(1, 0.5)
    }
}

class NormalGift extends Gift {
    constructor() {
        super(2, 1)
    }
}

class BigGift extends Gift {
    constructor() {
        super(5, 2)
    }
}


const giftFactory = (type) => {
    switch (type) {
        case "Small":
            return new SmallGift();
        case "Normal":
            return new NormalGift();
        case "Big":
            return new BigGift();
        default:
            return null;
    }
}
