import {
    giftFactory
} from './gift.js'
import {
    Sled
} from './sled.js'
import {
    Dwarf
} from './dwarf.js'

export class GiftsFactory {
    constructor() {
        this.dwarf = new Dwarf();
        this.sled = new Sled();
        this.message = ""
        this.sledMessage = ""
    }

    async createGift(type) {
        const gift = giftFactory(type)
        if (this.sled.isTooHeavy(gift)) {
            return this.message = "Sled too Heavy"
        } else {

            if (this.isDelivering) {
                return this.message = 'Dwarf says : the sled is not here, so why would I bother?!'
            } else {
                try {
                    if (!this.dwarf.getIsWorking()) {
                        this.dwarf.setIsWorking(true)
                        await this.dwarf.prepareGift(gift)
                        this.dwarf.addGift(this.sled, gift)
                    } else {
                        return this.message = "Dwarf is working"
                    }
                } catch (err) {
                    return this.message = err
                }
            }

        }
        return this.message = "okkk"

    }

    async displaySledStatus() {
        return this.message = await this.sled.deliver()
    }

}
