import {giftFactory} from './gift.js'
import {Sled} from './sled.js'
import {Dwarf} from './dwarf.js'
export class GiftsFactory {
    constructor() {
        this.dwarf = new Dwarf();
        this.sled = new Sled();
    }

    async createGift(){
        try {
            const smallGift = giftFactory("Small")
            if (!this.sled.isTooHeavy(smallGift) && !this.dwarf.getIsWorking()) {
                this.dwarf.setIsWorking(true)
                await this.dwarf.prepareGift(smallGift)
                this.dwarf.addGift(this.sled, smallGift)
                console.log(this.sled)
            }
        } catch (err) {
            console.log(err)
        }
    }
}
