import {giftFactory} from './gift.js'
import {Sled} from './sled.js'
import {Dwarf} from './dwarf.js'
export class GiftsFactory {
    constructor() {
        this.dwarf = new Dwarf();
        this.sled = new Sled();
    }

    async createGift(type){
        try {
            const gift = giftFactory(type)
            if (!this.sled.isTooHeavy(gift)) {
                if(!this.dwarf.getIsWorking()){
                this.dwarf.setIsWorking(true)
                await this.dwarf.prepareGift(gift)
                this.dwarf.addGift(this.sled, gift)
                console.log(this.sled)
                }
            }
        } catch (err) {
            console.log(err)
        }
    }
}
